import React, { useState, useEffect } from 'react';
import { X, Bell, Pin, Calendar, ExternalLink, ChevronDown, ChevronUp } from 'lucide-react';
import { useNotices, useNoticeActions } from '../../hooks/useNotices';
import { useUserStore } from '../../store/userStore';
import { useAuth } from '../../hooks/useAuth';
import type { Notice, NoticePriority } from '../../types/schema';
import { Button } from '../ui/button';
import { toSafeDate } from '../../utils/date';

interface NoticeModalProps {
  isOpen: boolean;
  onClose: () => void;
}

const PRIORITY_STYLES: Record<NoticePriority, {
  bg: string;
  text: string;
  border: string;
  icon: string;
  gradient: string;
}> = {
  LOW: {
    bg: 'bg-slate-50',
    text: 'text-slate-700',
    border: 'border-slate-200',
    icon: '??,
    gradient: 'from-slate-50 to-slate-100'
  },
  MEDIUM: {
    bg: 'bg-blue-50',
    text: 'text-blue-700',
    border: 'border-blue-200',
    icon: '??,
    gradient: 'from-blue-50 to-blue-100'
  },
  HIGH: {
    bg: 'bg-orange-50',
    text: 'text-orange-700',
    border: 'border-orange-200',
    icon: '??,
    gradient: 'from-orange-50 to-orange-100'
  },
  URGENT: {
    bg: 'bg-red-50',
    text: 'text-red-700',
    border: 'border-red-200',
    icon: '??,
    gradient: 'from-red-50 to-red-100'
  }
};

const PRIORITY_LABELS: Record<NoticePriority, Record<string, string>> = {
  LOW: { ko: '?쇰컲', en: 'Normal' },
  MEDIUM: { ko: '以묒슂', en: 'Important' },
  HIGH: { ko: '留ㅼ슦 以묒슂', en: 'Very Important' },
  URGENT: { ko: '湲닿툒', en: 'Urgent' }
};

export const NoticeModal: React.FC<NoticeModalProps> = ({ isOpen, onClose }) => {
  const { notices, loading } = useNotices();
  const { markAsRead } = useNoticeActions();
  const { language } = useUserStore();
  const { auth } = useAuth();
  const [expandedNotices, setExpandedNotices] = useState<Set<string>>(new Set());
  const [lightboxImage, setLightboxImage] = useState<string | null>(null);

  // Mark all notices as read when modal opens (only for authenticated users)
  useEffect(() => {
    if (isOpen && auth.user) {
      notices.forEach(notice => {
        if (notice.id) {
          // Silently mark as read - don't show errors to users
          markAsRead(notice.id).catch(err => {
            console.debug('[NoticeModal] Failed to mark notice as read:', err);
          });
        }
      });
    }
  }, [isOpen, notices, auth.user, markAsRead]);

  // Close on Escape key
  useEffect(() => {
    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === 'Escape' && isOpen) {
        onClose();
      }
    };
    window.addEventListener('keydown', handleEscape);
    return () => window.removeEventListener('keydown', handleEscape);
  }, [isOpen, onClose]);

  const toggleExpand = (noticeId: string) => {
    setExpandedNotices(prev => {
      const newSet = new Set(prev);
      if (newSet.has(noticeId)) {
        newSet.delete(noticeId);
      } else {
        newSet.add(noticeId);
      }
      return newSet;
    });
  };

  const getTitle = (notice: Notice) => notice.title[language] || notice.title.ko || notice.title.en || '';

  const formatDate = (timestamp: any) => {
    try {
      const date = toSafeDate(timestamp);
      if (!date) return '';
      return date.toLocaleDateString(language === 'ko' ? 'ko-KR' : 'en-US', {
        year: 'numeric',
        month: 'short',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch {
      return '';
    }
  };

  const handleBackdropClick = (e: React.MouseEvent) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  if (!isOpen) return null;

  return (
    <>
      {/* Backdrop with glassmorphism */}
      <div
        className="fixed inset-0 z-[100] flex items-center justify-center bg-black/40 backdrop-blur-md p-4 animate-in fade-in duration-300"
        onClick={handleBackdropClick}
      >
        {/* Modal Container */}
        <div className="bg-white rounded-3xl shadow-2xl w-full max-w-3xl max-h-[85vh] flex flex-col animate-in zoom-in-95 slide-in-from-bottom-4 duration-300 border border-white/50">
          {/* Header */}
          <div className="flex items-center justify-between p-6 border-b border-slate-100 bg-gradient-to-r from-slate-50 via-white to-slate-50 rounded-t-3xl">
            <div className="flex items-center gap-3">
              <div className="bg-gradient-to-br from-blue-500 to-blue-600 p-3 rounded-2xl shadow-lg shadow-blue-500/30">
                <Bell className="w-6 h-6 text-white" strokeWidth={2.5} />
              </div>
              <div>
                <h2 className="text-2xl font-bold text-slate-900">
                  {language === 'ko' ? '怨듭??ы빆' : 'Notices'}
                </h2>
                {notices.length > 0 && (
                  <p className="text-sm text-slate-500 mt-0.5 font-medium">
                    {notices.length} {language === 'ko' ? '媛쒖쓽 怨듭?' : 'notices'}
                  </p>
                )}
              </div>
            </div>
            <Button
              variant="ghost"
              size="icon"
              onClick={onClose}
              className="rounded-full hover:bg-slate-100 transition-all duration-200 hover:rotate-90"
              aria-label="Close"
            >
              <X className="w-6 h-6 text-slate-500" strokeWidth={2.5} />
            </Button>
          </div>

          {/* Content */}
          <div className="flex-1 overflow-y-auto p-6 custom-scrollbar">
            {loading ? (
              <div className="flex items-center justify-center py-16">
                <div className="relative">
                  <div className="animate-spin rounded-full h-14 w-14 border-4 border-slate-200 border-t-blue-600"></div>
                </div>
              </div>
            ) : notices.length === 0 ? (
              <div className="text-center py-16">
                <div className="bg-gradient-to-br from-slate-100 to-slate-50 w-20 h-20 rounded-2xl flex items-center justify-center mx-auto mb-5 shadow-inner">
                  <Bell className="w-10 h-10 text-slate-400" strokeWidth={2} />
                </div>
                <p className="text-slate-600 text-lg font-medium">
                  {language === 'ko' ? '怨듭??ы빆???놁뒿?덈떎.' : 'No notices yet.'}
                </p>
              </div>
            ) : (
              <div className="space-y-4">
                {notices.map((notice, index) => {
                  const isExpanded = expandedNotices.has(notice.id!);
                  const priority = notice.priority;
                  const styles = PRIORITY_STYLES[priority];

                  return (
                    <div
                      key={notice.id}
                      className={`group border rounded-2xl overflow-hidden transition-all duration-300 hover:shadow-xl ${styles.border} ${
                        isExpanded ? 'shadow-lg' : 'hover:border-opacity-60'
                      }`}
                      style={{ animationDelay: `${index * 50}ms` }}
                    >
                      {/* Notice Header */}
                      <div
                        className="p-5 cursor-pointer bg-white hover:bg-gradient-to-r hover:from-slate-50 hover:to-white transition-all duration-200"
                        onClick={() => toggleExpand(notice.id!)}
                        role="button"
                        tabIndex={0}
                        onKeyDown={(e) => e.key === 'Enter' && toggleExpand(notice.id!)}
                        aria-expanded={isExpanded}
                      >
                        <div className="flex items-start gap-4">
                          {/* Pin icon for pinned notices */}
                          {notice.isPinned && (
                            <div className="flex-shrink-0 mt-1">
                              <Pin className="w-5 h-5 text-red-500 fill-red-500" strokeWidth={2.5} />
                            </div>
                          )}

                          {/* Priority Badge */}
                          <div className={`flex-shrink-0 px-4 py-1.5 rounded-xl text-sm font-bold bg-gradient-to-r ${styles.gradient} ${styles.text} border ${styles.border} shadow-sm`}>
                            <span className="mr-1.5">{styles.icon}</span>
                            {PRIORITY_LABELS[priority][language] || PRIORITY_LABELS[priority].en}
                          </div>

                          {/* Title */}
                          <div className="flex-1 min-w-0">
                            <h3 className="font-bold text-slate-900 text-lg leading-snug group-hover:text-blue-600 transition-colors">
                              {getTitle(notice)}
                            </h3>

                            {/* Date */}
                            <div className="flex items-center gap-2 mt-2.5 text-sm text-slate-500">
                              <Calendar className="w-4 h-4" strokeWidth={2} />
                              <span className="font-medium">{formatDate(notice.createdAt)}</span>
                            </div>
                          </div>

                          {/* Expand/Collapse indicator */}
                          <div className="flex-shrink-0 ml-2">
                            <div className={`p-2.5 rounded-full transition-all duration-200 ${isExpanded ? 'bg-blue-100 text-blue-600' : 'bg-slate-100 text-slate-400 group-hover:bg-slate-200'}`}>
                              {isExpanded ? (
                                <ChevronUp className="w-5 h-5" strokeWidth={3} />
                              ) : (
                                <ChevronDown className="w-5 h-5" strokeWidth={3} />
                              )}
                            </div>
                          </div>
                        </div>

                        {/* Attachment indicators */}
                        {(notice.content.images && notice.content.images.length > 0 ||
                          notice.content.videos && notice.content.videos.length > 0) && (
                          <div className="flex items-center gap-4 mt-3.5 text-sm text-slate-500 font-medium">
                            {notice.content.images && notice.content.images.length > 0 && (
                              <span className="flex items-center gap-1.5 px-2 py-1 bg-slate-100 rounded-lg">
                                ?뼹截?{notice.content.images.length} {language === 'ko' ? '?μ쓽 ?대?吏' : 'images'}
                              </span>
                            )}
                            {notice.content.videos && notice.content.videos.length > 0 && (
                              <span className="flex items-center gap-1.5 px-2 py-1 bg-slate-100 rounded-lg">
                                ?렏 {notice.content.videos.length} {language === 'ko' ? '媛쒖쓽 ?숈쁺?? : 'videos'}
                              </span>
                            )}
                          </div>
                        )}
                      </div>

                      {/* Expanded Content */}
                      {isExpanded && (
                        <div className="px-5 pb-5 bg-gradient-to-b from-slate-50 to-white border-t border-slate-100 animate-in slide-in-from-top-2 duration-200">
                          {/* HTML Content */}
                          {notice.content.html && (
                            <div
                              className="prose prose-slate max-w-none text-slate-700 leading-relaxed mt-4 prose-headings:font-bold prose-headings:text-slate-900 prose-p:text-slate-700 prose-a:text-blue-600 prose-a:no-underline hover:prose-a:underline"
                              dangerouslySetInnerHTML={{ __html: notice.content.html }}
                            />
                          )}

                          {/* Images */}
                          {notice.content.images && notice.content.images.length > 0 && (
                            <div className="mt-5">
                              <div className="grid grid-cols-2 gap-3">
                                {notice.content.images.map((imageUrl, idx) => (
                                  <div
                                    key={idx}
                                    className="relative group aspect-video rounded-xl overflow-hidden cursor-pointer shadow-md hover:shadow-xl transition-all duration-200"
                                    onClick={() => setLightboxImage(imageUrl)}
                                  >
                                    <img
                                      src={imageUrl}
                                      alt={`${getTitle(notice)} ${idx + 1}`}
                                      className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
                                    />
                                    <div className="absolute inset-0 bg-black/0 group-hover:bg-black/30 transition-colors duration-200 flex items-center justify-center">
                                      <ExternalLink className="w-8 h-8 text-white opacity-0 group-hover:opacity-100 transition-opacity duration-200 drop-shadow-lg" strokeWidth={2.5} />
                                    </div>
                                  </div>
                                ))}
                              </div>
                            </div>
                          )}

                          {/* Videos */}
                          {notice.content.videos && notice.content.videos.length > 0 && (
                            <div className="mt-5 space-y-4">
                              {notice.content.videos.map((videoUrl, idx) => {
                                const youtubeMatch = videoUrl.match(/(?:youtube\.com\/watch\?v=|youtu\.be\/)([^&]+)/);
                                if (youtubeMatch) {
                                  return (
                                    <div key={idx} className="relative rounded-xl overflow-hidden shadow-lg bg-black aspect-video border border-slate-200">
                                      <iframe
                                        src={`https://www.youtube.com/embed/${youtubeMatch[1]}`}
                                        title={`${getTitle(notice)} - Video ${idx + 1}`}
                                        className="w-full h-full"
                                        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                                        allowFullScreen
                                      />
                                    </div>
                                  );
                                }

                                return (
                                  <div key={idx} className="flex items-center gap-3 p-4 bg-white rounded-xl border border-slate-200 shadow-sm hover:shadow-md transition-shadow">
                                    <div className="p-2.5 bg-red-100 rounded-xl">
                                      <ExternalLink className="w-5 h-5 text-red-600" strokeWidth={2.5} />
                                    </div>
                                    <a
                                      href={videoUrl}
                                      target="_blank"
                                      rel="noopener noreferrer"
                                      className="text-sm font-semibold text-blue-600 hover:text-blue-700 flex-1 truncate"
                                    >
                                      {language === 'ko' ? '?숈쁺??蹂닿린' : 'Watch Video'} {idx + 1}
                                    </a>
                                  </div>
                                );
                              })}
                            </div>
                          )}
                        </div>
                      )}
                    </div>
                  );
                })}
              </div>
            )}
          </div>

          {/* Footer */}
          <div className="p-5 border-t border-slate-100 bg-gradient-to-r from-slate-50 to-white rounded-b-3xl flex justify-end">
            <Button
              onClick={onClose}
              className="px-8 py-3 bg-gradient-to-r from-slate-700 to-slate-800 hover:from-slate-800 hover:to-slate-900 text-white rounded-xl font-bold shadow-lg hover:shadow-xl transition-all duration-200"
            >
              {language === 'ko' ? '?リ린' : 'Close'}
            </Button>
          </div>
        </div>
      </div>

      {/* Lightbox */}
      {lightboxImage && (
        <div
          className="fixed inset-0 z-[110] flex items-center justify-center bg-black/95 backdrop-blur-md p-4 animate-in fade-in duration-200"
          onClick={() => setLightboxImage(null)}
        >
          <img
            src={lightboxImage}
            alt="Enlarged view"
            className="max-w-full max-h-full object-contain rounded-2xl shadow-2xl animate-in zoom-in-95 duration-200"
            onClick={(e) => e.stopPropagation()}
          />
          <Button
            variant="ghost"
            size="icon"
            onClick={() => setLightboxImage(null)}
            className="absolute top-6 right-6 text-white hover:bg-white/20 rounded-full transition-all duration-200"
            aria-label="Close lightbox"
          >
            <X className="w-7 h-7" strokeWidth={2.5} />
          </Button>
        </div>
      )}
    </>
  );
};

export default NoticeModal;

import React, { useEffect, useState } from 'react';
import { useSearchParams } from 'react-router-dom';
import { doc, getDoc } from 'firebase/firestore';
import { db } from '../../firebase';
import { Loader2 } from 'lucide-react';
import toast from 'react-hot-toast';

interface InfraSettings {
    payment: {
        domestic: {
            secretKey: string;
        };
    };
}

const PaymentSuccessHandler: React.FC = () => {
    const [searchParams] = useSearchParams();
    const [isProcessing, setIsProcessing] = useState(true);

    useEffect(() => {
        // Prevent double execution using sessionStorage (persists across remounts)
        const processingKey = `payment_processing_${searchParams.get('paymentKey')}`;
        if (sessionStorage.getItem(processingKey)) {
            console.log("Payment already being processed, skipping duplicate call");
            return;
        }
        sessionStorage.setItem(processingKey, 'true');

        const processPayment = async () => {
            const paymentKey = searchParams.get('paymentKey');
            const orderId = searchParams.get('orderId');
            const amount = Number(searchParams.get('amount'));

            // Context Params passed from RegistrationPage
            const slug = searchParams.get('slug');
            const societyId = searchParams.get('societyId');
            const confId = searchParams.get('confId');
            const regId = searchParams.get('regId');

            if (!paymentKey || !orderId || !amount || !slug || !confId || !regId) {
                console.error("Missing params:", { paymentKey, orderId, amount, slug, confId, regId });
                toast.error("Invalid payment callback parameters.");
                setIsProcessing(false);
                return;
            }

            try {
                console.log("Processing Payment Success...", { orderId, amount, confId });

                // [Modified] Fetch registration data from sessionStorage (Garbage-Free Flow)
                // Since we didn't save PENDING doc in Firestore, we must use session data.
                const pendingDataStr = sessionStorage.getItem(`pending_reg_${orderId}`);

                let regData: any = null;
                if (pendingDataStr) {
                    try {
                        regData = JSON.parse(pendingDataStr);
                        console.log("Restored pending registration data from session");
                    } catch (e) {
                        console.error("Failed to parse pending data", e);
                    }
                }

                // Fallback: If not in session (e.g. cross-device?), try Firestore just in case (legacy support)
                if (!regData && regId) {
                    const regDoc = await getDoc(doc(db, 'conferences', confId, 'registrations', regId));
                    if (regDoc.exists()) {
                        regData = regDoc.data();
                        // Idempotency check: If already PAID in DB, redirect
                        if (regData.paymentStatus === 'PAID') {
                            console.log("Registration already paid (DB), redirecting");
                            const pureSlug = confId.includes('_') ? confId.split('_').slice(1).join('_') : confId;
                            const userName = regData.userInfo?.name || regData.name || '';
                            sessionStorage.removeItem(`payment_processing_${paymentKey}`);
                            window.location.href = `/${pureSlug}/register/success?orderId=${orderId}&name=${encodeURIComponent(userName)}`;
                            return;
                        }
                    }
                }

                if (!regData) {
                    console.error("Registration data not found (Session or DB)");
                    toast.error("?깅줉 ?뺣낫瑜?李얠쓣 ???놁뒿?덈떎. (?몄뀡 留뚮즺 ??");
                    setIsProcessing(false);
                    return;
                }

                const userData = {
                    name: regData.userInfo?.name || regData.name,
                    email: regData.userInfo?.email || regData.email,
                    phone: regData.userInfo?.phone || regData.phone,
                    affiliation: regData.userInfo?.affiliation || regData.affiliation,
                    userId: regData.userId || 'GUEST',
                    tier: regData.tier,
                    categoryName: regData.categoryName,
                    licenseNumber: regData.userInfo?.licenseNumber || '',
                    memberVerificationData: regData.memberVerificationData || null
                };

                // 1. FETCH SOCIETY INFRASTRUCTURE SETTINGS FOR PAYMENT KEYS
                let widgetSecretKey = "";
                if (societyId) {
                    try {
                        const infraRef = doc(db, 'societies', societyId, 'settings', 'infrastructure');
                        const infraSnap = await getDoc(infraRef);
                        if (infraSnap.exists()) {
                            const infraData = infraSnap.data() as InfraSettings;
                            widgetSecretKey = infraData.payment?.domestic?.secretKey || "";
                            console.log("Using society-specific payment key");
                        }
                    } catch (infraErr) {
                        console.warn("Failed to fetch infrastructure settings:", infraErr);
                    }
                }

                if (!widgetSecretKey) {
                    console.error("No payment secret key configured for this society");
                    toast.error("寃곗젣 ?ㅼ젙???щ컮瑜댁? ?딆뒿?덈떎. 愿由ъ옄?먭쾶 臾몄쓽?댁＜?몄슂.");
                    setIsProcessing(false);
                    return;
                }

                // 2. CALL CLOUD FUNCTION TO CONFIRM PAYMENT AND CREATE REGISTRATION
                // [FIX-20250124-01] CloudFunction handles both payment confirmation and registration creation
                // [FIX-20250124-CORS] Use HTTP POST endpoint with CORS support instead of callable
                // This ensures only paid registrations are stored in the DB
                const functionUrl = 'https://us-central1-eregi-8fc1e.cloudfunctions.net/confirmTossPaymentHttp';
                const response = await fetch(functionUrl, {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        paymentKey,
                        orderId,
                        amount,
                        secretKey: widgetSecretKey,
                        regId,
                        confId,
                        userData,
                        // [Fix-Option] Pass options to Cloud Function
                        baseAmount: regData.baseAmount || amount,
                        optionsTotal: regData.optionsTotal || 0,
                        selectedOptions: regData.selectedOptions || []
                    })
                });

                const result = await response.json();

                if (!response.ok) {
                    throw new Error(result.error || 'Payment confirmation failed');
                }

                const resData = result;
                if (resData.success) {
                    const paymentStatus = resData.data?.status;
                    const pureSlug = confId.includes('_') ? confId.split('_').slice(1).join('_') : confId;
                    sessionStorage.removeItem(`payment_processing_${paymentKey}`); // Cleanup

                    if (paymentStatus === 'WAITING_FOR_DEPOSIT') {
                        toast.success("媛?곴퀎醫뚭? 諛쒓툒?섏뿀?듬땲?? ?낃툑???꾨즺?댁＜?몄슂.");
                        // Redirect with virtual_account status
                        window.location.href = `/${pureSlug}/register/success?orderId=${orderId}&name=${encodeURIComponent(userData.name)}&status=virtual_account`;
                    } else {
                        toast.success("寃곗젣媛 ?꾨즺?섏뿀?듬땲??");
                        window.location.href = `/${pureSlug}/register/success?orderId=${orderId}&name=${encodeURIComponent(userData.name)}`;
                    }
                } else {
                    console.error("Payment Confirmation Failed:", resData);
                    toast.error("寃곗젣 ?뺤씤???ㅽ뙣?덉뒿?덈떎. 愿由ъ옄?먭쾶 臾몄쓽?댁＜?몄슂.");
                    sessionStorage.removeItem(`payment_processing_${paymentKey}`); // Cleanup
                    setIsProcessing(false);
                }

            } catch (error: unknown) {
                console.error("Payment Processing Error:", error);
                toast.error("寃곗젣 泥섎━ 以??ㅻ쪟媛 諛쒖깮?덉뒿?덈떎: " + (error instanceof Error ? error.message : "Unknown error"));
                sessionStorage.removeItem(`payment_processing_${paymentKey}`); // Cleanup
                setIsProcessing(false);
            }
        };

        processPayment();
    }, [searchParams]);

    if (isProcessing) {
        return (
            <div className="flex flex-col items-center justify-center min-h-screen bg-gray-50">
                <Loader2 className="w-12 h-12 text-blue-600 animate-spin mb-4" />
                <h2 className="text-xl font-semibold text-gray-800">寃곗젣 泥섎━ 以?..</h2>
                <p className="text-gray-500">李쎌쓣 ?レ? 留덉꽭??</p>
            </div>
        );
    }

    return (
        <div className="flex flex-col items-center justify-center min-h-screen bg-red-50">
            <h2 className="text-xl font-bold text-red-600">寃곗젣 泥섎━ ?ㅽ뙣</h2>
            <p>愿由ъ옄?먭쾶 臾몄쓽?댁＜?몄슂.</p>
        </div>
    );
};

export default PaymentSuccessHandler;

import { useState, useCallback, useMemo, useEffect } from 'react';
import type { ConferenceOption } from '../types/schema';

interface SelectedOption {
  option: ConferenceOption;
  quantity: number;
}

interface UsePricingResult {
  basePrice: number;
  selectedOptions: SelectedOption[];
  totalPrice: number;
  optionsTotal: number;
  setBasePrice: (price: number) => void;
  toggleOption: (option: ConferenceOption, quantity?: number) => void;
  updateOptionQuantity: (optionId: string, quantity: number) => void;
  clearOptions: () => void;
  isOptionSelected: (optionId: string) => boolean;
  getOptionQuantity: (optionId: string) => number;
}

export const usePricing = (initialBasePrice: number = 0): UsePricingResult => {
  const [basePrice, setBasePrice] = useState<number>(initialBasePrice);
  const [selectedOptions, setSelectedOptions] = useState<SelectedOption[]>([]);

  // Sync basePrice when initialBasePrice changes
  // Use useEffect for side effects instead of useState callback
  // [Fix] Removed useEffect that was resetting basePrice to initialBasePrice (0)
  // [Fix] The consumer should use setBasePrice to update the base price.

  // Calculate total price from options
  const optionsTotal = useMemo(() => {
    return selectedOptions.reduce((sum, { option, quantity }) => {
      return sum + option.price * quantity;
    }, 0);
  }, [selectedOptions]);

  // Total price including base fee
  const totalPrice = useMemo(() => {
    return basePrice + optionsTotal;
  }, [basePrice, optionsTotal]);

  // Toggle an option (add/remove)
  const toggleOption = useCallback((option: ConferenceOption, quantity: number = 1) => {
    setSelectedOptions((prev) => {
      const existingIndex = prev.findIndex((item) => item.option.id === option.id);

      if (existingIndex >= 0) {
        // Remove option if already selected
        return prev.filter((item) => item.option.id !== option.id);
      } else {
        // Add new option
        return [...prev, { option, quantity }];
      }
    });
  }, []);

  // Update quantity for a specific option
  const updateOptionQuantity = useCallback((optionId: string, quantity: number) => {
    if (quantity <= 0) {
      // Remove option if quantity is 0 or negative
      setSelectedOptions((prev) => prev.filter((item) => item.option.id !== optionId));
      return;
    }

    setSelectedOptions((prev) => {
      return prev.map((item) => {
        if (item.option.id === optionId) {
          // Check max quantity constraint
          const maxQty = item.option.maxQuantity || 1;
          const finalQuantity = Math.min(quantity, maxQty);
          return { ...item, quantity: finalQuantity };
        }
        return item;
      });
    });
  }, []);

  // Clear all selected options
  const clearOptions = useCallback(() => {
    setSelectedOptions([]);
  }, []);

  // Check if an option is selected
  const isOptionSelected = useCallback(
    (optionId: string): boolean => {
      return selectedOptions.some((item) => item.option.id === optionId);
    },
    [selectedOptions]
  );

  // Get quantity for a specific option
  const getOptionQuantity = useCallback(
    (optionId: string): number => {
      const found = selectedOptions.find((item) => item.option.id === optionId);
      return found?.quantity || 0;
    },
    [selectedOptions]
  );

  return {
    basePrice,
    selectedOptions,
    totalPrice,
    optionsTotal,
    setBasePrice,
    toggleOption,
    updateOptionQuantity,
    clearOptions,
    isOptionSelected,
    getOptionQuantity,
  };
};

import { useState, useEffect } from 'react';
import { collection, query, getDocs, Timestamp, limit, orderBy, startAfter, QueryDocumentSnapshot } from 'firebase/firestore';
import { db } from '../firebase';

// Define the root-level registration type based on PaymentSuccessHandler
export interface RootRegistration {
    id: string; // orderId
    orderId?: string; // Add optional orderId
    originalRegId: string;
    slug: string;
    societyId: string;
    conferenceId: string;
    userId: string;
    userName?: string; // Optional for safety
    userEmail?: string;
    userPhone?: string;
    userOrg?: string; // Optional
    affiliation?: string; // Optional
    tier: string; // Changed from grade to tier to match Firestore field
    categoryName?: string; // For fallback display
    amount: number;
    status: string; // 'PAID'
    paymentKey?: string;
    licenseNumber?: string; // Added
    paymentType?: string; // Added (e.g., '移대뱶', '怨꾩쥖?댁껜')
    method?: string; // Fallback for payment method
    paymentMethod?: string; // Direct field from Firestore
    createdAt: Timestamp;
    badgeIssued?: boolean;
    badgeIssuedAt?: Timestamp;
    virtualAccount?: {
        bank: string;
        accountNumber: string;
        customerName?: string;
        dueDate?: string;
    };
    options?: any[]; // Snapshot of selected options
}

interface UseRegistrationsPaginationParams {
    conferenceId: string | null;
    itemsPerPage?: number;
    searchQuery?: string; // 寃?됱뼱 (?대쫫, ?대찓?? ?꾪솕踰덊샇)
}


interface UseRegistrationsPaginationReturn {
    registrations: RootRegistration[];
    loading: boolean;
    error: string | null;
    currentPage: number;
    itemsPerPage: number;
    setCurrentPage: (page: number) => void;
    setItemsPerPage: (size: number) => void;
    lastVisible: QueryDocumentSnapshot | null;
    hasMore: boolean;
    refresh: () => void;
}

/**
 * Custom hook for paginated registration list
 * Follows project convention: use hooks for data access, not direct Firestore calls in pages
 *
 * @param conferenceId - The conference ID to fetch registrations for
 * @param itemsPerPage - Number of items per page (default: 50)
 * @returns Paginated registration data and controls
 */
export function useRegistrationsPagination({
    conferenceId,
    itemsPerPage: initialItemsPerPage = 50,
    searchQuery = ''
}: UseRegistrationsPaginationParams): UseRegistrationsPaginationReturn {
    const [registrations, setRegistrations] = useState<RootRegistration[]>([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);

    // Pagination states
    const [currentPage, setCurrentPage] = useState(1);
    const [itemsPerPage, setItemsPerPage] = useState(initialItemsPerPage);
    const [lastVisible, setLastVisible] = useState<QueryDocumentSnapshot | null>(null);
    const [hasMore, setHasMore] = useState(true);

    // Fetch data from Firestore with pagination
    useEffect(() => {
        const loadData = async () => {
            if (!conferenceId) {
                setLoading(false);
                return;
            }

            setLoading(true);
            setError(null);

            try {
                // Corrected Path: conferences/{conferenceId}/registrations
                const regRef = collection(db, 'conferences', conferenceId, 'registrations');

                // Build query with pagination
                let q = query(
                    regRef,
                    orderBy('createdAt', 'desc'),
                    limit(itemsPerPage)
                );

                // If loading next page, start after last document
                if (currentPage > 1 && lastVisible) {
                    q = query(
                        regRef,
                        orderBy('createdAt', 'desc'),
                        startAfter(lastVisible),
                        limit(itemsPerPage)
                    );
                }

                const snap = await getDocs(q);
                const data = snap.docs.map(d => {
                    const docData = d.data();
                    const flattened = {
                        id: d.id,
                        ...docData
                    } as RootRegistration;

                    // Ensure orderId is populated, fallback to id
                    if (!flattened.orderId) {
                        flattened.orderId = flattened.id;
                    }

                    // Flatten userInfo fields to top level for display
                    if (docData.userInfo) {
                        flattened.userName = docData.userInfo.name || docData.userName;
                        flattened.userEmail = docData.userInfo.email || docData.userEmail;
                        flattened.userPhone = docData.userInfo.phone || docData.userPhone;
                        flattened.affiliation = docData.userInfo.affiliation || docData.affiliation;
                        flattened.licenseNumber = docData.userInfo.licenseNumber || docData.licenseNumber;

                        // [Fix] Map grade/tier from userInfo if available
                        if (!flattened.tier && docData.userInfo.grade) {
                            flattened.tier = docData.userInfo.grade;
                        }
                    }

                    // [Fix] Map schema-defined userTier to tier if tier is missing
                    if (!flattened.tier && docData.userTier) {
                        flattened.tier = docData.userTier;
                    }

                    // [Fix] Fallback to categoryName if tier is still missing (for display)
                    if (!flattened.tier && docData.categoryName) {
                        flattened.tier = docData.categoryName;
                    }

                    // [Fix] Fallback for licenseNumber if still missing
                    if (!flattened.licenseNumber) {
                        if (docData.license) flattened.licenseNumber = docData.license;
                        else if (docData.userInfo?.licensenumber) flattened.licenseNumber = docData.userInfo.licensenumber;
                        // Check formData as last resort (some legacy data might be here)
                        else if (docData.formData?.licenseNumber) flattened.licenseNumber = docData.formData.licenseNumber;
                    }

                    return flattened;
                });

                // Update cursor states
                if (snap.docs.length > 0) {
                    setLastVisible(snap.docs[snap.docs.length - 1]);
                }

                // Check if more pages available
                setHasMore(snap.docs.length === itemsPerPage);

                // Client-side filtering by search query
                let filteredData = data;
                if (searchQuery && searchQuery.trim()) {
                    const query = searchQuery.toLowerCase().trim();
                    filteredData = data.filter(reg => {
                        const name = (reg.userName || '').toLowerCase();
                        const email = (reg.userEmail || '').toLowerCase();
                        const phone = (reg.userPhone || '').toLowerCase();
                        return name.includes(query) || email.includes(query) || phone.includes(query);
                    });
                }

                setRegistrations(filteredData);
            } catch (err: unknown) {
                setError((err instanceof Error ? err.message : 'Unknown error') + " (Check Console for Link)");
            } finally {
                setLoading(false);
            }
        };

        loadData();

        // lastVisible is intentionally excluded - it's updated during pagination and shouldn't trigger refetch
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [conferenceId, currentPage, itemsPerPage, searchQuery]);

    /**
     * Refresh data (reset to page 1)
     */
    const refresh = () => {
        setCurrentPage(1);
        setLastVisible(null);
    };

    /**
     * Handle page size change
     */
    const handleItemsPerPageChange = (newSize: number) => {
        setItemsPerPage(newSize);
        setCurrentPage(1);
        setLastVisible(null);
    };

    /**
     * Handle page change
     */
    const handlePageChange = (newPage: number) => {
        if (newPage === 1) {
            setCurrentPage(1);
            setLastVisible(null);
        } else if (newPage > currentPage && hasMore) {
            setCurrentPage(newPage);
        } else if (newPage < currentPage && newPage >= 1) {
            // For simplicity, reset to page 1 when going backward
            // A full implementation would track firstVisible for "Previous" functionality
            setCurrentPage(1);
            setLastVisible(null);
        }
    };

    return {
        registrations,
        loading,
        error,
        currentPage,
        itemsPerPage,
        setCurrentPage: handlePageChange,
        setItemsPerPage: handleItemsPerPageChange,
        lastVisible,
        hasMore,
        refresh
    };
}

import { useState, useEffect } from 'react';
import { doc, getDoc, getDocs, collection, query, where } from 'firebase/firestore';
import { db } from '../firebase';

/**
 * Translation hook for conference content
 * Handles multilingual content loading with caching
 */

// ??AGGRESSIVE OPTIMIZATION: Global cache
const translationCache = new Map<string, { data: unknown; timestamp: number }>();
const pendingTranslations = new Map<string, Promise<unknown>>();
const CACHE_TTL = 5 * 60 * 1000; // 5 minutes

export const clearTranslationCache = (slug?: string) => {
    if (slug) {
        translationCache.delete(slug);
        pendingTranslations.delete(slug);
    } else {
        translationCache.clear();
        pendingTranslations.clear();
    }
};

interface UseTranslationResult {
    t: (obj: unknown) => string;
    config: any; // Using any temporarily for backward compatibility with complex types
    loading: boolean;
    error: string | null;
    currentLang: string;
    setLanguage: (lang: string) => void;
    confId: string | null;
    urlSlug: string;
    refresh: () => void;
}

export const useTranslation = (slug: string): UseTranslationResult => {
    const [config, setConfig] = useState<any>(null);
    const [loading, setLoading] = useState<boolean>(true);
    const [error, setError] = useState<string | null>(null);
    const [currentLang, setCurrentLang] = useState<string>(localStorage.getItem('preferredLanguage') || 'ko');
    const [refreshTrigger, setRefreshTrigger] = useState<number>(0);

    const setLanguage = (lang: string) => {
        setCurrentLang(lang);
        localStorage.setItem('preferredLanguage', lang);
    };

    const t = (obj: unknown) => {
        if (!obj) return '';
        if (typeof obj === 'string') return obj;
        const localized = obj as Record<string, string>;
        return localized[currentLang === 'en' ? 'en' : 'ko'] || localized.ko || localized.en || '';
    };

    const refresh = () => setRefreshTrigger(prev => prev + 1);

    useEffect(() => {
        if (!slug) return;

        const cacheKey = `${slug}_${currentLang}`;
        const cached = translationCache.get(cacheKey);
        const now = Date.now();

        if (cached && (now - cached.timestamp < CACHE_TTL)) {
            setConfig(cached.data);
            setLoading(false);
            return;
        }

        const pending = pendingTranslations.get(cacheKey);
        if (pending) {
            pending.then(data => {
                setConfig(data);
                setLoading(false);
            });
            return;
        }

        setLoading(true);

        const fetchPromise = (async () => {
            try {
                // 1. Find Conference Document
                const q = query(collection(db, 'conferences'), where('slug', '==', slug));
                const querySnap = await getDocs(q);

                let confDoc = querySnap.docs[0];
                if (!confDoc) {
                    const directDoc = await getDoc(doc(db, 'conferences', slug));
                    if (directDoc.exists()) confDoc = directDoc;
                }

                if (!confDoc) throw new Error(`Conference not found: ${slug}`);

                const confId = confDoc.id;
                const confData = confDoc.data();
                const societyId = confData.societyId || confId.split('_')[0] || 'kadd';

                // 2. Parallel Fetch ALL required configurations
                const [identitySnap, societySnap, visualSnap, infoSnap] = await Promise.all([
                    getDoc(doc(db, 'conferences', confId, 'settings', 'identity')),
                    getDoc(doc(db, 'societies', societyId)),
                    getDoc(doc(db, 'conferences', confId, 'settings', 'visual')),
                    getDoc(doc(db, 'conferences', confId, 'info', 'general'))
                ]);

                // 3. Parallel Fetch Sub-collections (Performance Boost)
                const [pagesSnap, agendasSnap, speakersSnap, sponsorsSnap] = await Promise.all([
                    getDocs(collection(db, 'conferences', confId, 'pages')),
                    getDocs(collection(db, 'conferences', confId, 'agendas')),
                    getDocs(collection(db, 'conferences', confId, 'speakers')),
                    getDocs(collection(db, 'conferences', confId, 'sponsors'))
                ]);

                const combinedConfig = {
                    ...confData,
                    id: confId,
                    societyId,
                    identity: identitySnap.exists() ? identitySnap.data() : null,
                    society: societySnap.exists() ? societySnap.data() : null,
                    visualAssets: visualSnap.exists() ? visualSnap.data() : null,
                    info: infoSnap.exists() ? infoSnap.data() : null,
                    pages: pagesSnap.docs.map(d => ({ id: d.id, ...d.data() })),
                    agendas: agendasSnap.docs.map(d => ({ id: d.id, ...d.data() })),
                    speakers: speakersSnap.docs.map(d => ({ id: d.id, ...d.data() })),
                    sponsors: sponsorsSnap.docs.map(d => ({ id: d.id, ...d.data() })),
                };

                translationCache.set(cacheKey, { data: combinedConfig, timestamp: now });
                pendingTranslations.delete(cacheKey);
                return combinedConfig;

            } catch (err: unknown) {
                console.error('[useTranslation] Fetch error:', err);
                throw err;
            }
        })();

        pendingTranslations.set(cacheKey, fetchPromise);

        fetchPromise.then(data => {
            setConfig(data);
            setLoading(false);
            setError(null);
        }).catch(err => {
            setError((err as Error).message || 'Unknown error');
            setLoading(false);
        });

    }, [slug, refreshTrigger, currentLang]);

    return {
        t,
        config,
        loading,
        error,
        currentLang,
        setLanguage,
        confId: config?.id || null,
        urlSlug: slug,
        refresh
    };
};

