}

const statusToKorean = (status: string) => {
    switch (status) {
        case 'PAID': return '寃곗젣?꾨즺';
        case 'PENDING': return '?湲?;
        case 'WAITING_FOR_DEPOSIT': return '?낃툑?湲?;
        case 'PENDING_PAYMENT': return '寃곗젣吏꾪뻾以?;
        case 'REFUNDED': return '?섎텋?꾨즺';
        case 'REFUND_REQUESTED': return '?섎텋?붿껌';
        case 'CANCELED': return '痍⑥냼??;
        default: return status;
    }
};

